import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  value: {
    userinfo: "",
  },
};

export const counterSlice = createSlice({
  name: "user",
  initialState: {
    user: {},
    /* userList:[{
      firstname: "",
      lastname: "",
      carno: "",
      carmodel: "",
      phoneno: ""
    },] */
  },

  name1: 'changeName',
  initialState,

  reducers: {
    adduser: (state, action) => {
      state.user = action.payload;
      //(action.payload)
    },
    userlist: (state) => {
      state.value.userinfo = state.user;
    },
   addobjectinlist:(state, action) => {
    state.push(action.payload)

  },


     // updateobjectinlist:
    // delelteobjectinlist:
  },
});

export const { adduser, userlist ,addobjectinlist} = counterSlice.actions;

export const selectUser = (state) => state.user.user;

export default counterSlice.reducer;
