import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  value: {
    userinfo: "",
  },
};

export const counterSlice = createSlice({
  name: "user",
  initialState: {
    user: {},
    userList:[]
  },

  reducers: {
    adduser: (state, action) => {
      state.user = action.payload;
    },
    userlist: (state) => {
      state.value.userinfo = state.user;
    },
    // addobjectinlist:
     // updateobjectinlist:
    // delelte
  },
});

export const { adduser, userlist } = counterSlice.actions;

export const selectUser = (state) => state.user.user;

export default counterSlice.reducer;
