import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  value: {
    userinfo:'',
    useridx:'',
  },
}

export const counterSlice = createSlice({
  name: 'user',
  initialState : {
    user:{},
    userid:{},
  },
  initialState,
  reducers: {
      adduser: (state, action) => {
        // Redux Toolkit allows us to write "mutating" logic in reducers. It
        // doesn't actually mutate the state because it uses the Immer library,
        // which detects changes to a "draft state" and produces a brand new
        // immutable state based off those changes
        state.user = action.payload;
      },
      userlist: (state) => {
        state.value.userinfo =  state.user.lists
     },
      adduserid: (state, action) => {
        state.userid = action.payload;
      },
      adduseridget: (state) => {
        state.value.useridx =  state.userid
     },
     deleteuser: (state, action) => {
      state.user = state.user.filter(user=>user.id != action.payload.id)
    },
  },
})

// Action creators are generated for each case reducer function
export const {  adduser,userlist,adduserid,adduseridget ,deleteuser} = counterSlice.actions

export const selectUser = (state) => state.user.user;

export default counterSlice.reducer
