import { createStore} from 'react-redux';


const initialState= {
    Users : []
}


const reducer =(state = initialState, action) => {
  switch (action.type) {

  case Add_User:
    return { ...state, 
             Users:[...state.Users ,action.payload] }

  default:
    return state
  }
}

export default createStore(reducer);
